import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const navItems = [
  { path: '/admin/add-question', label: 'เพิ่มโจทย์' },
  { path: '/admin/add-solution', label: 'เพิ่มเฉลย' },
  { path: '/admin/manage-questions', label: 'จัดการโจทย์' },
  { path: '/admin/manage-solutions', label: 'จัดการเฉลย' },
];

function Navigation() {
  const location = useLocation();
  return (
    <nav className="bg-gradient-to-r from-blue-600 to-indigo-700 shadow-lg mb-8">
      <div className="max-w-7xl mx-auto px-4 py-3 flex justify-between items-center">
        <h1 className="text-xl font-bold text-white tracking-wide">Guru Web Admin</h1>
        <div className="flex space-x-4">
          {navItems.map(item => (
            <Link
              key={item.path}
              to={item.path}
              className={`px-4 py-2 rounded-lg font-medium transition-all duration-200 text-white hover:bg-white/20 ${location.pathname === item.path ? 'bg-white/30' : ''}`}
            >
              {item.label}
            </Link>
          ))}
        </div>
      </div>
    </nav>
  );
}

export default Navigation;
