import React, { useState } from 'react';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000/api';

function AdminAddSolution() {
  const [searchData, setSearchData] = useState({
    book_id: '',
    page: '',
    question_no: ''
  });
  const [recentBookIds, setRecentBookIds] = useState([]);
  const [selectedQuestion, setSelectedQuestion] = useState(null);
  const [answerText, setAnswerText] = useState('');
  const [answerImg, setAnswerImg] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  // ‡πÄ‡∏û‡∏¥‡πà‡∏° constants ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏Å‡∏≤‡∏£‡∏™‡∏£‡πâ‡∏≤‡∏á URL ‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û
  const IMAGE_BASE_URL = 'http://localhost:8000';

  // ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏´‡∏±‡∏™‡∏´‡∏ô‡∏±‡∏á‡∏™‡∏∑‡∏≠‡∏•‡πà‡∏≤‡∏™‡∏∏‡∏î‡∏à‡∏≤‡∏Å localStorage ‡πÄ‡∏°‡∏∑‡πà‡∏≠‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô
  React.useEffect(() => {
    const savedBookIds = localStorage.getItem('recent_book_ids');
    const lastBookId = localStorage.getItem('last_book_id');
    
    if (savedBookIds) {
      setRecentBookIds(JSON.parse(savedBookIds));
    }
    
    if (lastBookId) {
      setSearchData(prev => ({
        ...prev,
        book_id: lastBookId
      }));
    }
  }, []);

  const handleSearchInputChange = (e) => {
    const { name, value } = e.target;
    setSearchData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏£‡∏´‡∏±‡∏™‡∏´‡∏ô‡∏±‡∏á‡∏™‡∏∑‡∏≠‡πÉ‡∏ô localStorage
  const saveBookIdToHistory = (bookId) => {
    if (!bookId.trim()) return;
    
    // ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏£‡∏´‡∏±‡∏™‡∏´‡∏ô‡∏±‡∏á‡∏™‡∏∑‡∏≠‡∏•‡πà‡∏≤‡∏™‡∏∏‡∏î
    localStorage.setItem('last_book_id', bookId);
    
    // ‡∏≠‡∏±‡∏û‡πÄ‡∏î‡∏ó‡∏õ‡∏£‡∏∞‡∏ß‡∏±‡∏ï‡∏¥‡∏£‡∏´‡∏±‡∏™‡∏´‡∏ô‡∏±‡∏á‡∏™‡∏∑‡∏≠ (‡πÄ‡∏Å‡πá‡∏ö‡πÑ‡∏ß‡πâ 10 ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏•‡πà‡∏≤‡∏™‡∏∏‡∏î)
    const currentHistory = JSON.parse(localStorage.getItem('recent_book_ids') || '[]');
    const filteredHistory = currentHistory.filter(id => id !== bookId);
    const newHistory = [bookId, ...filteredHistory].slice(0, 10);
    
    localStorage.setItem('recent_book_ids', JSON.stringify(newHistory));
    setRecentBookIds(newHistory);
  };

  // ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏£‡∏´‡∏±‡∏™‡∏´‡∏ô‡∏±‡∏á‡∏™‡∏∑‡∏≠‡∏à‡∏≤‡∏Å‡∏õ‡∏£‡∏∞‡∏ß‡∏±‡∏ï‡∏¥
  const selectBookIdFromHistory = (bookId) => {
    setSearchData(prev => ({
      ...prev,
      book_id: bookId
    }));
  };

  const getImageUrl = (imagePath) => {
    if (!imagePath) return null;
    if (imagePath.startsWith('http')) return imagePath;
    return `${IMAGE_BASE_URL}/${imagePath}`;
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Ç‡∏ô‡∏≤‡∏î‡πÑ‡∏ü‡∏•‡πå (‡∏à‡∏≥‡∏Å‡∏±‡∏î‡∏ó‡∏µ‡πà 5MB)
      const maxSize = 5 * 1024 * 1024; // 5MB
      if (file.size > maxSize) {
        setMessage('‡πÑ‡∏ü‡∏•‡πå‡∏°‡∏µ‡∏Ç‡∏ô‡∏≤‡∏î‡πÉ‡∏´‡∏ç‡πà‡πÄ‡∏Å‡∏¥‡∏ô‡πÑ‡∏õ (‡∏à‡∏≥‡∏Å‡∏±‡∏î‡∏ó‡∏µ‡πà 5MB)');
        return;
      }

      // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡πÑ‡∏ü‡∏•‡πå
      const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/gif'];
      if (!allowedTypes.includes(file.type)) {
        setMessage('‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡πÑ‡∏ü‡∏•‡πå‡πÑ‡∏°‡πà‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö ‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡πÉ‡∏ä‡πâ JPG, PNG ‡∏´‡∏£‡∏∑‡∏≠ GIF');
        return;
      }

      setAnswerImg(file);
      
      // ‡∏™‡∏£‡πâ‡∏≤‡∏á preview ‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
      setMessage(''); // ‡∏•‡πâ‡∏≤‡∏á‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏° error
    }
  };

  const removeImage = () => {
    setAnswerImg(null);
    setImagePreview(null);
  };

  const handleAnswerTextChange = (e) => {
    setAnswerText(e.target.value);
  };

  const searchQuestion = async () => {
    setLoading(true);
    setMessage('');
    setSelectedQuestion(null);

    try {
      const response = await axios.get(
        `${API_BASE_URL}/qa/${searchData.book_id}/${searchData.page}/${searchData.question_no}`
      );
      setSelectedQuestion(response.data.question);
      
      // ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏£‡∏´‡∏±‡∏™‡∏´‡∏ô‡∏±‡∏á‡∏™‡∏∑‡∏≠‡πÄ‡∏°‡∏∑‡πà‡∏≠‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à
      saveBookIdToHistory(searchData.book_id);
      
      if (response.data.solution) {
        setMessage('‡πÇ‡∏à‡∏ó‡∏¢‡πå‡∏ô‡∏µ‡πâ‡∏°‡∏µ‡πÄ‡∏â‡∏•‡∏¢‡πÅ‡∏•‡πâ‡∏ß');
      }
    } catch (error) {
      setMessage('‡πÑ‡∏°‡πà‡∏û‡∏ö‡πÇ‡∏à‡∏ó‡∏¢‡πå‡∏ó‡∏µ‡πà‡∏£‡∏∞‡∏ö‡∏∏');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmitSolution = async (e) => {
    e.preventDefault();
    if (!selectedQuestion) return;

    setLoading(true);
    setMessage('');

    try {
      // ‡∏™‡∏£‡πâ‡∏≤‡∏á FormData ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡πÑ‡∏ü‡∏•‡πå
      const submitData = new FormData();
      submitData.append('question_id', selectedQuestion.id);
      submitData.append('answer_text', answerText || '');
      
      if (answerImg) {
        submitData.append('answer_img', answerImg);
      }

      const response = await axios.post(`${API_BASE_URL}/solutions`, submitData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setMessage('‡πÄ‡∏û‡∏¥‡πà‡∏°‡πÄ‡∏â‡∏•‡∏¢‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à‡πÅ‡∏•‡πâ‡∏ß!');
      setAnswerText('');
      setAnswerImg(null);
      setImagePreview(null);
      setSelectedQuestion(null);
      setSearchData({ book_id: '', page: '', question_no: '' });
    } catch (error) {
      setMessage('‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î: ' + (error.response?.data?.detail || error.message));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto">
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold text-gray-800 mb-6">‡πÄ‡∏û‡∏¥‡πà‡∏°‡πÄ‡∏â‡∏•‡∏¢</h2>
        
        {/* Search Question Form */}
        <div className="mb-6 p-4 bg-gray-50 rounded-lg">
          <h3 className="text-lg font-semibold text-gray-700 mb-4">‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡πÇ‡∏à‡∏ó‡∏¢‡πå</h3>
          
          <div className="grid grid-cols-3 gap-4 mb-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                ‡∏£‡∏´‡∏±‡∏™‡∏´‡∏ô‡∏±‡∏á‡∏™‡∏∑‡∏≠
              </label>
              <input
                type="text"
                name="book_id"
                value={searchData.book_id}
                onChange={handleSearchInputChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                placeholder="MATH-101"
              />
              
              {/* ‡πÅ‡∏™‡∏î‡∏á‡∏õ‡∏£‡∏∞‡∏ß‡∏±‡∏ï‡∏¥‡∏£‡∏´‡∏±‡∏™‡∏´‡∏ô‡∏±‡∏á‡∏™‡∏∑‡∏≠ */}
              {recentBookIds.length > 0 && (
                <div className="mt-2">
                  <label className="block text-xs font-medium text-gray-500 mb-1">
                    ‡∏£‡∏´‡∏±‡∏™‡∏•‡πà‡∏≤‡∏™‡∏∏‡∏î:
                  </label>
                  <div className="flex flex-wrap gap-1">
                    {recentBookIds.slice(0, 5).map((bookId, index) => (
                      <button
                        key={index}
                        type="button"
                        onClick={() => selectBookIdFromHistory(bookId)}
                        className="px-2 py-1 text-xs bg-blue-100 text-blue-700 rounded hover:bg-blue-200 transition-colors"
                        title={`‡∏Ñ‡∏•‡∏¥‡∏Å‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÉ‡∏ä‡πâ‡∏£‡∏´‡∏±‡∏™: ${bookId}`}
                      >
                        {bookId}
                      </button>
                    ))}
                  </div>
                </div>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                ‡∏´‡∏ô‡πâ‡∏≤
              </label>
              <input
                type="number"
                name="page"
                value={searchData.page}
                onChange={handleSearchInputChange}
                min="1"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                placeholder="25"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                ‡∏Ç‡πâ‡∏≠‡∏ó‡∏µ‡πà
              </label>
              <input
                type="number"
                name="question_no"
                value={searchData.question_no}
                onChange={handleSearchInputChange}
                min="1"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                placeholder="1"
              />
            </div>
          </div>

          <button
            onClick={searchQuestion}
            disabled={loading || !searchData.book_id || !searchData.page || !searchData.question_no}
            className="w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50"
          >
            {loading ? '‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤...' : '‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡πÇ‡∏à‡∏ó‡∏¢‡πå'}
          </button>
          
          <p className="text-xs text-gray-500 text-center mt-2">
            üí° ‡∏£‡∏∞‡∏ö‡∏ö‡∏à‡∏∞‡∏à‡∏≥‡∏£‡∏´‡∏±‡∏™‡∏´‡∏ô‡∏±‡∏á‡∏™‡∏∑‡∏≠‡∏ó‡∏µ‡πà‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à‡πÑ‡∏ß‡πâ‡πÉ‡∏´‡πâ‡∏≠‡∏±‡∏ï‡πÇ‡∏ô‡∏°‡∏±‡∏ï‡∏¥
          </p>
        </div>

        {/* Selected Question Display */}
        {selectedQuestion && (
          <div className="mb-6 p-4 bg-blue-50 rounded-lg border border-blue-200">
            <h3 className="text-lg font-semibold text-blue-800 mb-2">‡πÇ‡∏à‡∏ó‡∏¢‡πå‡∏ó‡∏µ‡πà‡∏û‡∏ö</h3>
            <p><strong>‡∏£‡∏´‡∏±‡∏™‡∏´‡∏ô‡∏±‡∏á‡∏™‡∏∑‡∏≠:</strong> {selectedQuestion.book_id}</p>
            <p><strong>‡∏´‡∏ô‡πâ‡∏≤:</strong> {selectedQuestion.page}</p>
            <p><strong>‡∏Ç‡πâ‡∏≠‡∏ó‡∏µ‡πà:</strong> {selectedQuestion.question_no}</p>
            {selectedQuestion.question_text && (
              <p><strong>‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏°‡πÇ‡∏à‡∏ó‡∏¢‡πå:</strong> {selectedQuestion.question_text}</p>
            )}
            {selectedQuestion.question_img && (
              <div className="mt-2">
                <strong>‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û‡πÇ‡∏à‡∏ó‡∏¢‡πå:</strong>
                <img 
                  src={getImageUrl(selectedQuestion.question_img)} 
                  alt="Question" 
                  className="mt-2 max-w-full h-auto rounded border"
                  onError={(e) => {
                    e.target.style.display = 'none';
                    e.target.nextSibling.style.display = 'block';
                  }}
                />
                <div style={{display: 'none'}} className="text-red-500 text-sm mt-2">
                  ‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û‡πÇ‡∏à‡∏ó‡∏¢‡πå‡πÑ‡∏î‡πâ
                </div>
              </div>
            )}
          </div>
        )}

        {/* Add Solution Form */}
        {selectedQuestion && (
          <form onSubmit={handleSubmitSolution} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                ‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö‡πÅ‡∏ö‡∏ö‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏° (Answer Text)
              </label>
              <textarea
                value={answerText}
                onChange={handleAnswerTextChange}
                rows="4"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                placeholder="‡∏û‡∏¥‡∏°‡∏û‡πå‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö‡∏Ç‡πâ‡∏≠‡∏ó‡∏µ‡πà‡∏ô‡∏µ‡πà (‡∏ñ‡πâ‡∏≤‡∏°‡∏µ)"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                ‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û‡πÄ‡∏â‡∏•‡∏¢ (Answer Image)
              </label>
              <div className="space-y-3">
                <input
                  type="file"
                  accept="image/jpeg,image/jpg,image/png,image/gif"
                  onChange={handleImageChange}
