import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import AdminAddQuestion from './pages/AdminAddQuestion';
import AdminAddSolution from './pages/AdminAddSolution';
import AdminManageQuestions from './pages/AdminManageQuestions';
import AdminManageSolutions from './pages/AdminManageSolutions';
import StudentSearch from './pages/StudentSearch';

function App() {
  return (
    <Router>
      <div className="min-h-screen bg-gray-100">
        {/* Navigation */}
        <nav className="bg-primary-600 text-white shadow-lg">
          <div className="container mx-auto px-4">
            <div className="flex justify-between items-center py-4">
              <h1 className="text-2xl font-bold">Guru Web - ระบบโจทย์-เฉลย</h1>
              <div className="space-x-4">
                <Link to="/" className="hover:text-primary-200">หน้าหลัก</Link>
                <Link to="/admin/add-question" className="hover:text-primary-200">เพิ่มโจทย์</Link>
                <Link to="/admin/add-solution" className="hover:text-primary-200">เพิ่มเฉลย</Link>
                <Link to="/admin/manage-questions" className="hover:text-primary-200">จัดการโจทย์</Link>
                <Link to="/admin/manage-solutions" className="hover:text-primary-200">จัดการเฉลย</Link>
              </div>
            </div>
          </div>
        </nav>

        {/* Main Content */}
        <main className="container mx-auto px-4 py-8">
          <Routes>
            <Route path="/" element={<StudentSearch />} />
            <Route path="/admin/add-question" element={<AdminAddQuestion />} />
            <Route path="/admin/add-solution" element={<AdminAddSolution />} />
            <Route path="/admin/manage-questions" element={<AdminManageQuestions />} />
            <Route path="/admin/manage-solutions" element={<AdminManageSolutions />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
