import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navigation from '../components/Navigation';

const API_BASE_URL = 'http://localhost:8000/api';
const IMAGE_BASE_URL = 'http://localhost:8000';

function AdminManageSolutions() {
  const [solutions, setSolutions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [editingSolution, setEditingSolution] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);
  const [formData, setFormData] = useState({
    answer_text: '',
    answer_img: null
  });
  const [imagePreview, setImagePreview] = useState(null);

  useEffect(() => {
    fetchSolutions();
  }, []);

  const fetchSolutions = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/solutions`);
      setSolutions(response.data);
    } catch (error) {
      setMessage('ไม่สามารถโหลดรายการเฉลยได้');
    } finally {
      setLoading(false);
    }
  };

  const getImageUrl = (imagePath) => {
    if (!imagePath) return null;
    if (imagePath.startsWith('http')) return imagePath;
    return `${IMAGE_BASE_URL}/${imagePath}`;
  };

  const handleDelete = async (solutionId) => {
    if (!window.confirm('คุณต้องการลบเฉลยนี้หรือไม่?')) return;

    try {
      await axios.delete(`${API_BASE_URL}/solutions/${solutionId}`);
      setMessage('ลบเฉลยสำเร็จแล้ว');
      fetchSolutions();
    } catch (error) {
      setMessage('ไม่สามารถลบเฉลยได้');
    }
  };

  const handleEdit = (solution) => {
    setEditingSolution(solution);
    setFormData({
      answer_text: solution.answer_text || '',
      answer_img: null
    });
    setImagePreview(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const maxSize = 5 * 1024 * 1024;
      if (file.size > maxSize) {
        setMessage('ไฟล์มีขนาดใหญ่เกินไป (จำกัดที่ 5MB)');
        return;
      }

      const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/gif'];
      if (!allowedTypes.includes(file.type)) {
        setMessage('ประเภทไฟล์ไม่รองรับ กรุณาใช้ JPG, PNG หรือ GIF');
        return;
      }

      setFormData(prev => ({
        ...prev,
        answer_img: file
      }));
      
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);
      setMessage('');
    }
  };

  const removeImage = () => {
    setFormData(prev => ({
      ...prev,
      answer_img: null
    }));
    setImagePreview(null);
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    
    if (!formData.answer_text.trim() && !formData.answer_img) {
      setMessage('กรุณากรอกคำตอบหรือเลือกรูปภาพ');
      return;
    }

    setLoading(true);
    setMessage('');

    try {
      const submitData = new FormData();
      submitData.append('answer_text', formData.answer_text || '');
      
      if (formData.answer_img) {
        submitData.append('answer_img', formData.answer_img);
      }

      await axios.put(`${API_BASE_URL}/solutions/${editingSolution.id}`, submitData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setMessage('อัปเดตเฉลยสำเร็จแล้ว');
      setEditingSolution(null);
      setFormData({
        answer_text: '',
        answer_img: null
      });
      setImagePreview(null);
      fetchSolutions();
    } catch (error) {
      console.error('Error updating solution:', error);
      setMessage('ไม่สามารถอัปเดตเฉลยได้');
    } finally {
      setLoading(false);
    }
  };

  const cancelEdit = () => {
    setEditingSolution(null);
    setFormData({
      answer_text: '',
      answer_img: null
    });
    setImagePreview(null);
    setMessage('');
  };

  // Filter and pagination
  const filteredSolutions = solutions.filter(solution =>
    solution.question?.book_id?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    solution.answer_text?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    solution.question?.page?.toString().includes(searchTerm) ||
    solution.question?.question_no?.toString().includes(searchTerm)
  );

  const totalPages = Math.ceil(filteredSolutions.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentSolutions = filteredSolutions.slice(startIndex, endIndex);

  const goToPage = (page) => {
    setCurrentPage(page);
  };

  const nextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-purple-900 to-indigo-900">
      <Navigation />
      {/* Header */}
      <div className="bg-white/10 backdrop-blur-sm border-b border-white/20">
        <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
          <div className="flex items-center space-x-3">
            <div className="w-10 h-10 bg-gradient-to-r from-indigo-400 to-indigo-600 rounded-lg flex items-center justify-center">
              <span className="text-white font-bold text-xl">🔧</span>
            </div>
            <h1 className="text-2xl font-bold text-white">จัดการเฉลย</h1>
          </div>
          <button
            onClick={() => window.history.back()}
            className="bg-white/20 hover:bg-white/30 text-white px-4 py-2 rounded-lg border border-white/30 transition-all duration-200 backdrop-blur-sm"
          >
            ← กลับ
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 py-8">
        {!editingSolution ? (
          // Solutions List View
          <div className="space-y-6">
            {/* Search and Stats */}
            <div className="bg-white/10 backdrop-blur-md border border-white/20 rounded-3xl shadow-2xl p-8">
              <div className="text-center mb-8">
                <div className="w-16 h-16 bg-gradient-to-r from-indigo-400 to-indigo-600 rounded-2xl mx-auto mb-4 flex items-center justify-center">
                  <span className="text-3xl">🔍</span>
                </div>
                <h2 className="text-3xl font-bold text-white mb-2">จัดการเฉลย</h2>
                <p className="text-white/70">ค้นหา แก้ไข และลบเฉลย</p>
                <div className="w-16 h-1 bg-gradient-to-r from-indigo-400 to-blue-500 rounded-full mx-auto mt-4"></div>
              </div>

              {/* Search Bar */}
              <div className="mb-6">
                <div className="relative">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <span className="text-white/60">🔍</span>
                  </div>
                  <input
                    type="text"
                    placeholder="ค้นหาเฉลย (รหัสหนังสือ, หน้า, ข้อที่, คำตอบ...)"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="block w-full pl-10 pr-3 py-3 bg-white/10 border border-white/30 rounded-xl text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-indigo-400 focus:border-transparent backdrop-blur-sm transition-all duration-200"
                  />
                </div>
              </div>

              {/* Stats */}
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
                <div className="bg-blue-500/20 rounded-xl p-4 border border-blue-400/30">
                  <div className="text-center">
                    <span className="text-2xl font-bold text-blue-200">{solutions.length}</span>
                    <p className="text-blue-300 text-sm">เฉลยทั้งหมด</p>
                  </div>
                </div>
                <div className="bg-green-500/20 rounded-xl p-4 border border-green-400/30">
                  <div className="text-center">
                    <span className="text-2xl font-bold text-green-200">{filteredSolutions.length}</span>
                    <p className="text-green-300 text-sm">ผลการค้นหา</p>
                  </div>
                </div>
                <div className="bg-indigo-500/20 rounded-xl p-4 border border-indigo-400/30">
                  <div className="text-center">
                    <span className="text-2xl font-bold text-indigo-200">{totalPages}</span>
                    <p className="text-indigo-300 text-sm">หน้าทั้งหมด</p>
                  </div>
                </div>
              </div>
            </div>

            {/* Solutions Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {loading ? (
                <div className="col-span-full flex justify-center items-center py-12">
                  <div className="bg-white/10 backdrop-blur-md border border-white/20 rounded-2xl p-8 text-center">
                    <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white mx-auto mb-4"></div>
                    <p className="text-white">กำลังโหลด...</p>
                  </div>
                </div>
              ) : currentSolutions.length === 0 ? (
                <div className="col-span-full text-center py-12">
                  <div className="bg-white/10 backdrop-blur-md border border-white/20 rounded-2xl p-8">
                    <span className="text-6xl mb-4 block">✅</span>
                    <p className="text-white/70 text-lg">ไม่พบเฉลย</p>
                  </div>
                </div>
              ) : (
                currentSolutions.map((solution) => (
                  <div key={solution.id} className="bg-white/10 backdrop-blur-md border border-white/20 rounded-2xl shadow-lg hover:shadow-xl transition-all duration-300 hover:scale-[1.02] overflow-hidden">
                    <div className="p-6">
                      {/* Solution Header */}
                      <div className="flex justify-between items-start mb-4">
                        <div className="bg-indigo-500/20 rounded-lg p-2 border border-indigo-400/30">
                          <span className="text-indigo-200 text-sm font-medium">ID: {solution.id}</span>
                        </div>
                        <div className="flex space-x-2">
                          <button
                            onClick={() => handleEdit(solution)}
                            className="bg-blue-500/20 hover:bg-blue-500/30 text-blue-200 hover:text-white p-2 rounded-lg border border-blue-400/30 transition-all duration-200"
                            title="แก้ไข"
                          >
                            ✏️
                          </button>
                          <button
                            onClick={() => handleDelete(solution.id)}
                            className="bg-red-500/20 hover:bg-red-500/30 text-red-200 hover:text-white p-2 rounded-lg border border-red-400/30 transition-all duration-200"
                            title="ลบ"
                          >
                            🗑️
                          </button>
                        </div>
                      </div>

                      {/* Question Info */}
                      {solution.question && (
                        <div className="space-y-3 mb-4">
                          <div className="bg-yellow-500/20 rounded-lg p-3 border border-yellow-400/30">
                            <span className="text-yellow-200 text-sm font-medium">📋 โจทย์ที่เฉลย:</span>
                          </div>
                          <div className="grid grid-cols-2 gap-2 text-sm">
                            <div className="bg-blue-500/10 rounded-lg p-2 border border-blue-400/20">
                              <span className="text-blue-200">📚 หนังสือ:</span>
                              <p className="text-white font-medium truncate">{solution.question.book_id}</p>
                            </div>
                            <div className="bg-green-500/10 rounded-lg p-2 border border-green-400/20">
                              <span className="text-green-200">📄 หน้า:</span>
                              <p className="text-white font-medium">{solution.question.page}</p>
                            </div>
                          </div>
                          <div className="bg-purple-500/10 rounded-lg p-2 border border-purple-400/20">
                            <span className="text-purple-200 text-sm">🔢 ข้อที่:</span>
                            <p className="text-white font-medium">{solution.question.question_no}</p>
                          </div>
                        </div>
                      )}

                      {/* Answer Text */}
                      {solution.answer_text && (
                        <div className="mb-4">
                          <div className="bg-green-500/20 rounded-lg p-3 border border-green-400/30 mb-2">
                            <span className="text-green-200 text-sm font-medium">✏️ คำตอบ:</span>
                          </div>
                          <div className="bg-white/5 rounded-lg p-3 border border-white/10">
                            <p className="text-white/90 text-sm line-clamp-4">
                              {solution.answer_text}
                            </p>
                          </div>
                        </div>
                      )}

                      {/* Answer Image */}
                      {solution.answer_img && (
                        <div className="mb-4">
                          <div className="bg-blue-500/20 rounded-lg p-2 border border-blue-400/30 mb-2">
                            <span className="text-blue-200 text-sm font-medium">🖼️ รูปภาพคำตอบ:</span>
                          </div>
                          <div className="bg-white/5 rounded-lg p-2 border border-white/10">
                            <img
                              src={getImageUrl(solution.answer_img)}
                              alt="Answer"
                              className="w-full h-32 object-cover rounded-lg"
                            />
                          </div>
                        </div>
                      )}

                      {/* Created At */}
                      <div className="text-xs text-white/50 mt-4 text-center">
                        📅 {new Date(solution.created_at).toLocaleString('th-TH')}
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>

            {/* Pagination */}
            {totalPages > 1 && (
              <div className="bg-white/10 backdrop-blur-md border border-white/20 rounded-2xl p-6">
                <div className="flex justify-center items-center space-x-2">
                  <button
                    onClick={prevPage}
                    disabled={currentPage === 1}
                    className="bg-white/20 hover:bg-white/30 disabled:opacity-50 disabled:cursor-not-allowed text-white px-4 py-2 rounded-lg border border-white/30 transition-all duration-200"
                  >
                    ← ก่อนหน้า
                  </button>
                  
                  <div className="flex space-x-1">
                    {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                      const page = i + 1;
                      return (
                        <button
                          key={page}
                          onClick={() => goToPage(page)}
                          className={`w-10 h-10 rounded-lg border transition-all duration-200 ${
                            currentPage === page
                              ? 'bg-indigo-500 border-indigo-400 text-white'
                              : 'bg-white/20 hover:bg-white/30 border-white/30 text-white'
                          }`}
                        >
                          {page}
                        </button>
                      );
                    })}
                  </div>

                  <button
                    onClick={nextPage}
                    disabled={currentPage === totalPages}
                    className="bg-white/20 hover:bg-white/30 disabled:opacity-50 disabled:cursor-not-allowed text-white px-4 py-2 rounded-lg border border-white/30 transition-all duration-200"
                  >
                    ถัดไป →
                  </button>
                </div>
                
                <p className="text-center text-white/70 text-sm mt-4">
                  หน้า {currentPage} จาก {totalPages} ({filteredSolutions.length} รายการ)
                </p>
              </div>
            )}
          </div>
        ) : (
          // Edit Form
          <div className="bg-white/10 backdrop-blur-md border border-white/20 rounded-3xl shadow-2xl p-8">
            <div className="text-center mb-8">
              <div className="w-16 h-16 bg-gradient-to-r from-green-400 to-green-600 rounded-2xl mx-auto mb-4 flex items-center justify-center">
                <span className="text-3xl">✏️</span>
              </div>
              <h2 className="text-3xl font-bold text-white mb-2">แก้ไขเฉลย</h2>
              <p className="text-white/70">แก้ไขข้อมูลเฉลย ID: {editingSolution.id}</p>
              <div className="w-16 h-1 bg-gradient-to-r from-green-400 to-blue-500 rounded-full mx-auto mt-4"></div>
            </div>

            {/* Question Info Display */}
            {editingSolution.question && (
              <div className="bg-white/5 rounded-2xl p-6 border border-white/10 mb-8">
                <div className="text-center mb-4">
                  <div className="w-12 h-12 bg-gradient-to-r from-yellow-400 to-orange-500 rounded-xl mx-auto mb-3 flex items-center justify-center">
                    <span className="text-2xl">📋</span>
                  </div>
                  <h3 className="text-xl font-bold text-white">โจทย์ที่กำลังแก้ไขเฉลย</h3>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
                  <div className="bg-blue-500/20 rounded-lg p-3 border border-blue-400/30">
                    <span className="text-blue-200">📚 รหัสหนังสือ:</span>
                    <span className="text-white font-medium ml-2">{editingSolution.question.book_id}</span>
                  </div>
                  <div className="bg-green-500/20 rounded-lg p-3 border border-green-400/30">
                    <span className="text-green-200">📄 หน้า:</span>
                    <span className="text-white font-medium ml-2">{editingSolution.question.page}</span>
                  </div>
                  <div className="bg-purple-500/20 rounded-lg p-3 border border-purple-400/30">
                    <span className="text-purple-200">🔢 ข้อที่:</span>
                    <span className="text-white font-medium ml-2">{editingSolution.question.question_no}</span>
                  </div>
                </div>
              </div>
            )}

            <form onSubmit={handleUpdate} className="space-y-6">
              <div>
                <label className="block text-sm font-medium text-white/90 mb-2">
                  ✏️ คำตอบ (ข้อความ)
                </label>
                <textarea
                  name="answer_text"
                  value={formData.answer_text}
                  onChange={handleInputChange}
                  rows="6"
                  className="block w-full px-4 py-3 bg-white/10 border border-white/30 rounded-xl text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-green-400 focus:border-transparent backdrop-blur-sm transition-all duration-200 resize-none"
                  placeholder="พิมพ์คำตอบที่นี่..."
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-white/90 mb-2">
                  🖼️ รูปภาพคำตอบ (Answer Image)
                </label>
                <div className="space-y-3">
                  <input
                    type="file"
                    onChange={handleImageChange}
                    accept="image/jpeg,image/jpg,image/png,image/gif"
                    className="block w-full px-4 py-3 bg-white/10 border border-white/30 rounded-xl text-white file:mr-4 file:py-2 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-medium file:bg-green-500 file:text-white hover:file:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-400 focus:border-transparent backdrop-blur-sm transition-all duration-200"
                  />

                  {/* Current Image */}
                  {editingSolution.answer_img && !imagePreview && (
                    <div className="bg-white/5 rounded-xl p-4 border border-white/20">
                      <p className="text-white/70 text-sm mb-2">รูปภาพปัจจุบัน:</p>
                      <img
                        src={getImageUrl(editingSolution.answer_img)}
                        alt="Current Answer"
                        className="max-w-full h-auto max-h-64 mx-auto rounded-lg border border-white/30"
                      />
                    </div>
                  )}

                  {/* New Image Preview */}
                  {imagePreview && (
                    <div className="relative bg-white/5 rounded-xl p-4 border border-white/20">
                      <p className="text-white/70 text-sm mb-2">รูปภาพใหม่:</p>
                      <img
                        src={imagePreview}
                        alt="New Preview"
                        className="max-w-full h-auto max-h-64 mx-auto rounded-lg border border-white/30"
                      />
                      <button
                        type="button"
                        onClick={removeImage}
                        className="absolute top-6 right-6 bg-red-500/80 hover:bg-red-500 text-white rounded-full p-2 transition-all duration-200 backdrop-blur-sm"
                      >
                        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                        </svg>
                      </button>
                    </div>
                  )}

                  <p className="text-sm text-white/60">
                    รองรับไฟล์ JPG, PNG, GIF ขนาดไม่เกิน 5MB
                  </p>
                </div>
              </div>

              <div className="flex space-x-4">
                <button
                  type="submit"
                  disabled={loading || (!formData.answer_text.trim() && !formData.answer_img)}
                  className="flex-1 flex justify-center items-center py-3 px-4 border border-transparent text-sm font-medium rounded-xl text-white bg-gradient-to-r from-green-500 to-blue-600 hover:from-green-600 hover:to-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200 shadow-lg hover:shadow-xl transform hover:scale-[1.02]"
                >
                  {loading ? (
                    <>
                      <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                      กำลังอัปเดต...
                    </>
                  ) : (
                    <>
                      <span className="mr-2">💾</span>
                      อัปเดตเฉลย
                    </>
                  )}
                </button>

                <button
                  type="button"
                  onClick={cancelEdit}
                  className="flex-1 flex justify-center items-center py-3 px-4 border border-white/30 text-sm font-medium rounded-xl text-white bg-white/10 hover:bg-white/20 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-white/50 transition-all duration-200 backdrop-blur-sm"
                >
                  <span className="mr-2">❌</span>
                  ยกเลิก
                </button>
              </div>
            </form>
          </div>
        )}

        {/* Message Display */}
        {message && (
          <div className={`p-4 rounded-xl backdrop-blur-sm ${
            message.includes('สำเร็จ') 
              ? 'bg-green-500/20 border border-green-400/30' 
              : 'bg-red-500/20 border border-red-400/30'
          }`}>
            <div className="flex items-center">
              <span className={`mr-2 ${message.includes('สำเร็จ') ? 'text-green-300' : 'text-red-300'}`}>
                {message.includes('สำเร็จ') ? '✅' : '⚠️'}
              </span>
              <p className={`text-sm ${message.includes('สำเร็จ') ? 'text-green-200' : 'text-red-200'}`}>
                {message}
              </p>
            </div>
          </div>
        )}
      </div>

      {/* Decorative Elements */}
      <div className="absolute top-10 left-10 w-20 h-20 bg-indigo-400/20 rounded-full blur-xl"></div>
      <div className="absolute bottom-10 right-10 w-32 h-32 bg-blue-400/20 rounded-full blur-xl"></div>
      <div className="absolute top-1/2 right-0 w-24 h-24 bg-green-400/20 rounded-full blur-xl"></div>
    </div>
  );
}

export default AdminManageSolutions;
