import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000/api';
const IMAGE_BASE_URL = 'http://localhost:8000';

function AdminManageQuestions() {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [editingQuestion, setEditingQuestion] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);
  const [formData, setFormData] = useState({
    book_id: '',
    page: '',
    question_no: '',
    question_text: '',
    question_img: null
  });
  const [imagePreview, setImagePreview] = useState(null);

  useEffect(() => {
    fetchQuestions();
  }, []);

  const fetchQuestions = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/questions`);
      setQuestions(response.data);
    } catch (error) {
      setMessage('ไม่สามารถโหลดรายการโจทย์ได้');
    } finally {
      setLoading(false);
    }
  };

  const getImageUrl = (imagePath) => {
    if (!imagePath) return null;
    if (imagePath.startsWith('http')) return imagePath;
    return `${IMAGE_BASE_URL}/${imagePath}`;
  };

  const handleDelete = async (questionId) => {
    if (!window.confirm('คุณต้องการลบโจทย์นี้หรือไม่?')) return;

    try {
      await axios.delete(`${API_BASE_URL}/questions/${questionId}`);
      setMessage('ลบโจทย์สำเร็จแล้ว');
      fetchQuestions();
    } catch (error) {
      setMessage('เกิดข้อผิดพลาดในการลบโจทย์');
    }
  };

  const handleEdit = (question) => {
    setEditingQuestion(question);
    setFormData({
      book_id: question.book_id,
      page: question.page.toString(),
      question_no: question.question_no.toString(),
      question_text: question.question_text || '',
      question_img: null
    });
    if (question.question_img) {
      setImagePreview(getImageUrl(question.question_img));
    }
  };

  const handleCancelEdit = () => {
    setEditingQuestion(null);
    setFormData({
      book_id: '',
      page: '',
      question_no: '',
      question_text: '',
      question_img: null
    });
    setImagePreview(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // ตรวจสอบขนาดไฟล์ (จำกัดที่ 5MB)
      const maxSize = 5 * 1024 * 1024; // 5MB
      if (file.size > maxSize) {
        setMessage('ไฟล์มีขนาดใหญ่เกินไป (จำกัดที่ 5MB)');
        return;
      }

      // ตรวจสอบประเภทไฟล์
      const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/gif'];
      if (!allowedTypes.includes(file.type)) {
        setMessage('ประเภทไฟล์ไม่รองรับ กรุณาใช้ JPG, PNG หรือ GIF');
        return;
      }

      setFormData(prev => ({
        ...prev,
        question_img: file
      }));
      
      // แสดงตัวอย่างรูปภาพ
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);
      setMessage(''); // ล้างข้อความ error
    }
  };

  const removeImage = () => {
    setFormData(prev => ({
      ...prev,
      question_img: null
    }));
    setImagePreview(null);
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      // สร้าง FormData สำหรับอัปโหลดไฟล์
      const submitData = new FormData();
      submitData.append('book_id', formData.book_id);
      submitData.append('page', parseInt(formData.page));
      submitData.append('question_no', parseInt(formData.question_no));
      submitData.append('question_text', formData.question_text || '');
      
      if (formData.question_img) {
        submitData.append('question_img', formData.question_img);
      }

      await axios.put(`${API_BASE_URL}/questions/${editingQuestion.id}`, submitData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setMessage('แก้ไขโจทย์สำเร็จแล้ว!');
      handleCancelEdit();
      fetchQuestions();
    } catch (error) {
      console.error('Error updating question:', error);
      if (error.response) {
        setMessage('เกิดข้อผิดพลาด: ' + (error.response.data.detail || error.response.data.message || 'ไม่สามารถแก้ไขโจทย์ได้'));
      } else {
        setMessage('เกิดข้อผิดพลาด: Network Error');
      }
    } finally {
      setLoading(false);
    }
  };

  const filteredQuestions = questions.filter(question =>
    question.book_id.toLowerCase().includes(searchTerm.toLowerCase()) ||
    question.question_text?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    question.page.toString().includes(searchTerm) ||
    question.question_no.toString().includes(searchTerm)
  );

  // Pagination
  const totalPages = Math.ceil(filteredQuestions.length / itemsPerPage);
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentQuestions = filteredQuestions.slice(indexOfFirstItem, indexOfLastItem);

  // รีเซ็ตหน้าเมื่อค้นหา
  const handleSearch = (value) => {
    setSearchTerm(value);
    setCurrentPage(1);
  };

  return (
    <div className="max-w-6xl mx-auto">
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold text-gray-800 mb-6">จัดการโจทย์ทั้งหมด</h2>
        
        {/* Search Bar */}
        <div className="mb-6">
          <input
            type="text"
            placeholder="ค้นหาโจทย์ (รหัสหนังสือ, หน้า, ข้อที่, หรือข้อความโจทย์)"
            value={searchTerm}
            onChange={(e) => handleSearch(e.target.value)}
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
          />
        </div>

        {/* Edit Form Modal */}
        {editingQuestion && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white rounded-lg p-6 max-w-2xl w-full max-h-screen overflow-y-auto m-4">
              <h3 className="text-xl font-bold text-gray-800 mb-4">แก้ไขโจทย์</h3>
              
              <form onSubmit={handleUpdate} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    รหัสหนังสือ (Book ID) *
                  </label>
                  <input
                    type="text"
                    name="book_id"
                    value={formData.book_id}
                    onChange={handleInputChange}
                    required
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      หน้า (Page) *
                    </label>
                    <input
                      type="number"
                      name="page"
                      value={formData.page}
                      onChange={handleInputChange}
                      required
                      min="1"
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      ข้อที่ (Question No.) *
                    </label>
                    <input
                      type="number"
                      name="question_no"
                      value={formData.question_no}
                      onChange={handleInputChange}
                      required
                      min="1"
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    ข้อความโจทย์ (Question Text)
                  </label>
                  <textarea
                    name="question_text"
                    value={formData.question_text}
                    onChange={handleInputChange}
                    rows="4"
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    รูปภาพโจทย์ (Question Image)
                  </label>
                  <div className="space-y-3">
                    <input
                      type="file"
                      onChange={handleImageChange}
                      accept="image/jpeg,image/jpg,image/png,image/gif"
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                    />
                    
                    {imagePreview && (
                      <div className="relative">
                        <img
                          src={imagePreview}
                          alt="Preview"
                          className="max-w-full h-auto max-h-64 mx-auto rounded-md border border-gray-300"
                        />
                        <button
                          type="button"
                          onClick={removeImage}
                          className="absolute top-2 right-2 bg-red-500 text-white rounded-full p-1 hover:bg-red-600 focus:outline-none"
                        >
                          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                          </svg>
                        </button>
                      </div>
                    )}
                  </div>
                </div>

                <div className="flex space-x-4">
                  <button
                    type="submit"
                    disabled={loading}
                    className="flex-1 bg-primary-600 text-white py-2 px-4 rounded-md hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-primary-500 disabled:opacity-50"
                  >
                    {loading ? 'กำลังบันทึก...' : 'บันทึกการแก้ไข'}
                  </button>
                  <button
                    type="button"
                    onClick={handleCancelEdit}
                    className="flex-1 bg-gray-500 text-white py-2 px-4 rounded-md hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-gray-500"
                  >
                    ยกเลิก
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* ข้อมูลจำนวนและหน้า */}
        {filteredQuestions.length > 0 && (
          <div className="mb-4">
            <p className="text-gray-600">
              แสดง {indexOfFirstItem + 1}-{Math.min(indexOfLastItem, filteredQuestions.length)} จาก {filteredQuestions.length} รายการ (ทั้งหมด {questions.length} รายการ)
            </p>
          </div>
        )}

        {/* Questions List */}
        {loading ? (
          <div className="text-center py-8">
            <div className="text-lg">กำลังโหลด...</div>
          </div>
        ) : (
          <div className="space-y-4">
            {filteredQuestions.length === 0 ? (
              <div className="text-center py-8 text-gray-500">
                ไม่พบโจทย์ในระบบ
              </div>
            ) : (
              currentQuestions.map((question) => (
                <div key={question.id} className="border border-gray-200 rounded-lg p-4">
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <div className="flex items-center space-x-4 mb-2">
                        <h3 className="text-lg font-semibold text-gray-800">
                          {question.book_id} หน้า {question.page} ข้อ {question.question_no}
                        </h3>
                        <span className="text-sm text-gray-500">
                          ID: {question.id}
                        </span>
                      </div>
                      
                      {question.question_text && (
                        <p className="text-gray-600 mb-3">
                          {question.question_text}
                        </p>
                      )}
                      
                      {question.question_img && (
                        <div className="mb-3">
                          <img
                            src={getImageUrl(question.question_img)}
                            alt="Question"
                            className="max-w-xs h-auto rounded border"
                            onError={(e) => {
                              e.target.style.display = 'none';
                            }}
                          />
                        </div>
                      )}
                    </div>
                    
                    <div className="flex space-x-2 ml-4">
                      <button
                        onClick={() => handleEdit(question)}
                        className="bg-blue-500 text-white px-3 py-1 rounded text-sm hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                      >
                        แก้ไข
                      </button>
                      <button
                        onClick={() => handleDelete(question.id)}
                        className="bg-red-500 text-white px-3 py-1 rounded text-sm hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-red-500"
                      >
                        ลบ
                      </button>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        )}

        {/* Pagination */}
        {totalPages > 1 && (
          <div className="mt-6 flex justify-center items-center space-x-2">
            <button
              onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
              disabled={currentPage === 1}
              className="px-3 py-2 bg-gray-300 text-gray-700 rounded-md hover:bg-gray-400 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              หน้าก่อน
            </button>
            
            <div className="flex space-x-1">
              {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
                <button
                  key={page}
                  onClick={() => setCurrentPage(page)}
                  className={`px-3 py-2 rounded-md ${
                    currentPage === page
                      ? 'bg-blue-500 text-white'
                      : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                  }`}
                >
                  {page}
                </button>
              ))}
            </div>
            
            <button
              onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
              disabled={currentPage === totalPages}
              className="px-3 py-2 bg-gray-300 text-gray-700 rounded-md hover:bg-gray-400 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              หน้าถัดไป
            </button>
          </div>
        )}

        {message && (
          <div className={`mt-4 p-4 rounded-md ${
            message.includes('สำเร็จ') 
              ? 'bg-green-100 text-green-700 border border-green-300' 
              : 'bg-red-100 text-red-700 border border-red-300'
          }`}>
            {message}
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminManageQuestions;
