            {error && (
              <div className="bg-red-500/20 border border-red-400/30 rounded-xl p-4 backdrop-blur-sm">
                <div className="flex items-center">
                  <span className="text-red-300 mr-2">‚ö†Ô∏è</span>
                  <p className="text-red-200 text-sm">{error}</p>
