import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const navItems = [
  { to: '/admin/add-question', label: 'เพิ่มโจทย์' },
  { to: '/admin/add-solution', label: 'เพิ่มเฉลย' },
  { to: '/admin/manage-questions', label: 'จัดการโจทย์' },
  { to: '/admin/manage-solutions', label: 'จัดการเฉลย' },
];

function Navigation() {
  const location = useLocation();
  return (
    <nav className="bg-blue-600 text-white shadow-lg mb-8">
      <div className="max-w-7xl mx-auto px-4">
        <div className="flex justify-between items-center py-4">
          <h1 className="text-2xl font-bold">Guru Web - ระบบโจทย์-เฉลย</h1>
          <div className="flex items-center space-x-4">
            {navItems.map(item => (
              <Link
                key={item.to}
                to={item.to}
                className={`hover:bg-blue-700 px-4 py-2 rounded transition-all duration-150 ${location.pathname === item.to ? 'bg-blue-800 font-bold' : ''}`}
              >
                {item.label}
              </Link>
            ))}
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navigation;
