import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminManageSolutions = () => {
    const [solutions, setSolutions] = useState([]);
    const [questions, setQuestions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');
    const [editingSolution, setEditingSolution] = useState(null);
    const [showEditModal, setShowEditModal] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [itemsPerPage] = useState(10);
    const [formData, setFormData] = useState({
        question_id: '',
        answer_text: '',
        answer_img: null
    });

    // ‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÄ‡∏â‡∏•‡∏¢‡πÅ‡∏•‡∏∞‡πÇ‡∏à‡∏ó‡∏¢‡πå
    useEffect(() => {
        loadSolutions();
        loadQuestions();
    }, []);

    const loadSolutions = async () => {
        try {
            setLoading(true);
            // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÄ‡∏â‡∏•‡∏¢‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î
            const response = await axios.get('http://localhost:8000/api/solutions');
            setSolutions(response.data);
        } catch (error) {
            console.error('Error loading solutions:', error);
            alert('‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÄ‡∏â‡∏•‡∏¢');
        } finally {
            setLoading(false);
        }
    };

    const loadQuestions = async () => {
        try {
            const response = await axios.get('http://localhost:8000/api/questions');
            setQuestions(response.data);
        } catch (error) {
            console.error('Error loading questions:', error);
        }
    };

    // ‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡πÄ‡∏â‡∏•‡∏¢
    const filteredSolutions = solutions.filter(solution => {
        const question = questions.find(q => q.id === solution.question_id);
        const searchLower = searchTerm.toLowerCase();
        
        return (
            solution.answer_text?.toLowerCase().includes(searchLower) ||
            question?.question_text?.toLowerCase().includes(searchLower) ||
            question?.book_id?.toLowerCase().includes(searchLower) ||
            question?.page?.toString().includes(searchLower) ||
            question?.question_no?.toString().includes(searchLower)
        );
    });

    // Pagination
    const totalPages = Math.ceil(filteredSolutions.length / itemsPerPage);
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    const currentSolutions = filteredSolutions.slice(startIndex, endIndex);

    // ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤‡∏ñ‡∏±‡∏î‡πÑ‡∏õ/‡∏Å‡πà‡∏≠‡∏ô‡∏´‡∏ô‡πâ‡∏≤
    const goToNextPage = () => {
        if (currentPage < totalPages) {
            setCurrentPage(currentPage + 1);
        }
    };

    const goToPrevPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };

    const goToPage = (page) => {
        setCurrentPage(page);
    };

    // ‡∏£‡∏µ‡πÄ‡∏ã‡πá‡∏ï‡∏´‡∏ô‡πâ‡∏≤‡πÄ‡∏°‡∏∑‡πà‡∏≠‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤
    useEffect(() => {
        setCurrentPage(1);
    }, [searchTerm]);

    // ‡πÄ‡∏õ‡∏¥‡∏î‡πÇ‡∏°‡∏î‡∏≠‡∏•‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç
    const openEditModal = (solution) => {
        setEditingSolution(solution);
        setFormData({
            question_id: solution.question_id,
            answer_text: solution.answer_text || '',
            answer_img: null
        });
        setShowEditModal(true);
    };

    // ‡∏õ‡∏¥‡∏î‡πÇ‡∏°‡∏î‡∏≠‡∏•‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç
    const closeEditModal = () => {
        setShowEditModal(false);
        setEditingSolution(null);
        setFormData({
            question_id: '',
            answer_text: '',
            answer_img: null
        });
    };

    // ‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏Å‡∏≤‡∏£‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô‡πÅ‡∏õ‡∏•‡∏á‡πÉ‡∏ô‡∏ü‡∏≠‡∏£‡πå‡∏°
    const handleInputChange = (e) => {
        const { name, value, files } = e.target;
        
        if (name === 'answer_img') {
            setFormData(prev => ({
                ...prev,
                answer_img: files[0] || null
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    // ‡∏≠‡∏±‡∏û‡πÄ‡∏î‡∏ó‡πÄ‡∏â‡∏•‡∏¢
    const handleUpdateSolution = async (e) => {
        e.preventDefault();
        
        if (!formData.answer_text.trim() && !formData.answer_img) {
            alert('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö‡πÅ‡∏ö‡∏ö‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏°‡∏´‡∏£‡∏∑‡∏≠‡∏≠‡∏±‡∏û‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û');
            return;
        }

        try {
            const updateData = new FormData();
            updateData.append('question_id', formData.question_id);
            updateData.append('answer_text', formData.answer_text);
            
            if (formData.answer_img) {
                updateData.append('answer_img', formData.answer_img);
            }

            await axios.put(`http://localhost:8000/api/solutions/${editingSolution.id}`, updateData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });

            alert('‡∏≠‡∏±‡∏û‡πÄ‡∏î‡∏ó‡πÄ‡∏â‡∏•‡∏¢‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à!');
            closeEditModal();
            loadSolutions();
        } catch (error) {
            console.error('Error updating solution:', error);
            alert('‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏≠‡∏±‡∏û‡πÄ‡∏î‡∏ó‡πÄ‡∏â‡∏•‡∏¢');
        }
    };

    // ‡∏•‡∏ö‡πÄ‡∏â‡∏•‡∏¢
    const handleDeleteSolution = async (solutionId) => {
        if (!window.confirm('‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà‡∏ó‡∏µ‡πà‡∏à‡∏∞‡∏•‡∏ö‡πÄ‡∏â‡∏•‡∏¢‡∏ô‡∏µ‡πâ?')) {
            return;
        }

        try {
            await axios.delete(`http://localhost:8000/api/solutions/${solutionId}`);
            alert('‡∏•‡∏ö‡πÄ‡∏â‡∏•‡∏¢‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à!');
            loadSolutions();
        } catch (error) {
            console.error('Error deleting solution:', error);
            alert('‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏•‡∏ö‡πÄ‡∏â‡∏•‡∏¢');
        }
    };

    // ‡∏´‡∏≤‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÇ‡∏à‡∏ó‡∏¢‡πå‡∏à‡∏≤‡∏Å question_id
    const getQuestionInfo = (questionId) => {
        return questions.find(q => q.id === questionId);
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-gray-100 flex items-center justify-center">
                <div className="text-xl">‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î...</div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gray-100 p-6">
            <div className="max-w-7xl mx-auto">
                <div className="bg-white rounded-lg shadow-md p-6">
                    <h1 className="text-3xl font-bold text-gray-800 mb-6">‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡πÄ‡∏â‡∏•‡∏¢</h1>
                    
                    {/* ‡∏ä‡πà‡∏≠‡∏á‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤ */}
                    <div className="mb-6">
                        <input
                            type="text"
                            placeholder="‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡πÄ‡∏â‡∏•‡∏¢ (‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö, ‡πÇ‡∏à‡∏ó‡∏¢‡πå, ‡∏£‡∏´‡∏±‡∏™‡∏´‡∏ô‡∏±‡∏á‡∏™‡∏∑‡∏≠, ‡∏´‡∏ô‡πâ‡∏≤, ‡∏Ç‡πâ‡∏≠)"
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                        />
                    </div>

                    {/* ‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡πÄ‡∏â‡∏•‡∏¢‡πÅ‡∏•‡∏∞ pagination info */}
                    <div className="mb-4 flex justify-between items-center">
                        <p className="text-gray-600">
                            ‡∏û‡∏ö‡πÄ‡∏â‡∏•‡∏¢ {filteredSolutions.length} ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£ ‡∏à‡∏≤‡∏Å‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î {solutions.length} ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£
                            {filteredSolutions.length > 0 && (
                                <span className="ml-2 text-sm">
                                    (‡πÅ‡∏™‡∏î‡∏á {startIndex + 1}-{Math.min(endIndex, filteredSolutions.length)} ‡∏à‡∏≤‡∏Å {filteredSolutions.length})
                                </span>
                            )}
                        </p>
                        {totalPages > 1 && (
                            <p className="text-gray-600 text-sm">
                                ‡∏´‡∏ô‡πâ‡∏≤ {currentPage} ‡∏à‡∏≤‡∏Å {totalPages}
                            </p>
                        )}
                    </div>

                    {/* ‡∏ï‡∏≤‡∏£‡∏≤‡∏á‡πÄ‡∏â‡∏•‡∏¢ */}
                    <div className="overflow-x-auto">
                        <table className="min-w-full bg-white border border-gray-200">
