import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000/api';

function AdminAddQuestion() {
  const [formData, setFormData] = useState({
    book_id: '',
    page: '',
    question_no: '',
    question_text: '',
    question_img: null
  });

  // โหลดรหัสหนังสือจาก localStorage เมื่อ component mount
  useEffect(() => {
    const savedBookId = localStorage.getItem('lastBookId');
    if (savedBookId) {
      setFormData(prev => ({
        ...prev,
        book_id: savedBookId
      }));
    }
  }, []);
  const [imagePreview, setImagePreview] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // บันทึกรหัสหนังสือลง localStorage เมื่อมีการเปลี่ยนแปลง
    if (name === 'book_id' && value.trim()) {
      localStorage.setItem('lastBookId', value.trim());
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // ตรวจสอบขนาดไฟล์ (จำกัดที่ 5MB)
      const maxSize = 5 * 1024 * 1024; // 5MB
      if (file.size > maxSize) {
        setMessage('ไฟล์มีขนาดใหญ่เกินไป (จำกัดที่ 5MB)');
        return;
      }

      // ตรวจสอบประเภทไฟล์
      const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/gif'];
      if (!allowedTypes.includes(file.type)) {
        setMessage('ประเภทไฟล์ไม่รองรับ กรุณาใช้ JPG, PNG หรือ GIF');
        return;
      }

      setFormData(prev => ({
        ...prev,
        question_img: file
      }));
      
      // แสดงตัวอย่างรูปภาพ
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);
      setMessage(''); // ล้างข้อความ error
    }
  };

  const removeImage = () => {
    setFormData(prev => ({
      ...prev,
      question_img: null
    }));
    setImagePreview(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      // สร้าง FormData สำหรับอัปโหลดไฟล์
      const submitData = new FormData();
      submitData.append('book_id', formData.book_id);
      submitData.append('page', parseInt(formData.page));
      submitData.append('question_no', parseInt(formData.question_no));
      submitData.append('question_text', formData.question_text || '');
      
      if (formData.question_img) {
        submitData.append('question_img', formData.question_img);
      }

      const response = await axios.post(`${API_BASE_URL}/questions`, submitData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setMessage('เพิ่มโจทย์สำเร็จแล้ว!');
      setFormData({
        book_id: '',
        page: '',
        question_no: '',
        question_text: '',
        question_img: null
      });
      setImagePreview(null);
    } catch (error) {
      console.error('Error submitting question:', error);
      if (error.response) {
        setMessage('เกิดข้อผิดพลาด: ' + (error.response.data.detail || error.response.data.message || 'ไม่สามารถเพิ่มโจทย์ได้'));
      } else {
        setMessage('เกิดข้อผิดพลาด: Network Error');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto">
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold text-gray-800 mb-6">เพิ่มโจทย์ใหม่</h2>
        
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              รหัสหนังสือ (Book ID) *
              {localStorage.getItem('lastBookId') && (
                <span className="text-xs text-gray-500 ml-2">
                  (ล่าสุด: {localStorage.getItem('lastBookId')})
                </span>
              )}
            </label>
            <input
              type="text"
              name="book_id"
              value={formData.book_id}
              onChange={handleInputChange}
              required
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
              placeholder={localStorage.getItem('lastBookId') ? `ล่าสุด: ${localStorage.getItem('lastBookId')}` : "เช่น MATH-101"}
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                หน้า (Page) *
              </label>
              <input
                type="number"
                name="page"
                value={formData.page}
                onChange={handleInputChange}
                required
                min="1"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                placeholder="เช่น 25"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                ข้อที่ (Question No.) *
              </label>
              <input
                type="number"
                name="question_no"
                value={formData.question_no}
                onChange={handleInputChange}
                required
                min="1"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                placeholder="เช่น 1"
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              ข้อความโจทย์ (Question Text)
            </label>
            <textarea
              name="question_text"
              value={formData.question_text}
              onChange={handleInputChange}
              rows="4"
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
              placeholder="พิมพ์ข้อความโจทย์ที่นี่ (ถ้ามี)"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              รูปภาพโจทย์ (Question Image)
            </label>
            <div className="space-y-3">
              <input
                type="file"
                onChange={handleImageChange}
                accept="image/jpeg,image/jpg,image/png,image/gif"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
              />
              
              {imagePreview && (
                <div className="relative">
                  <img
                    src={imagePreview}
                    alt="Preview"
                    className="max-w-full h-auto max-h-64 mx-auto rounded-md border border-gray-300"
                  />
                  <button
                    type="button"
                    onClick={removeImage}
                    className="absolute top-2 right-2 bg-red-500 text-white rounded-full p-1 hover:bg-red-600 focus:outline-none"
                  >
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                    </svg>
                  </button>
                </div>
              )}
              
              <p className="text-sm text-gray-500">
                รองรับไฟล์ JPG, PNG, GIF ขนาดไม่เกิน 5MB
              </p>
            </div>
          </div>

          <button
            type="submit"
            disabled={loading}
            className="w-full bg-primary-600 text-white py-3 px-4 rounded-md hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-primary-500 disabled:opacity-50 font-medium"
          >
            {loading ? 'กำลังเพิ่มโจทย์...' : 'เพิ่มโจทย์'}
          </button>
        </form>

        {message && (
          <div className={`mt-4 p-4 rounded-md ${
            message.includes('สำเร็จ') 
              ? 'bg-green-100 text-green-700 border border-green-300' 
              : 'bg-red-100 text-red-700 border border-red-300'
          }`}>
            {message}
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminAddQuestion;
