import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminManageSolutions = () => {
    const [solutions, setSolutions] = useState([]);
    const [questions, setQuestions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');
    const [editingSolution, setEditingSolution] = useState(null);
    const [showEditModal, setShowEditModal] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [itemsPerPage] = useState(10);
    const [formData, setFormData] = useState({
        question_id: '',
        answer_text: '',
        answer_img: null
    });

    // โหลดข้อมูลเฉลยและโจทย์
    useEffect(() => {
        loadSolutions();
        loadQuestions();
    }, []);

    const loadSolutions = async () => {
        try {
            setLoading(true);
            // ดึงข้อมูลเฉลยทั้งหมด
            const response = await axios.get('http://localhost:8000/api/solutions');
            setSolutions(response.data);
        } catch (error) {
            console.error('Error loading solutions:', error);
            alert('เกิดข้อผิดพลาดในการโหลดข้อมูลเฉลย');
        } finally {
            setLoading(false);
        }
    };

    const loadQuestions = async () => {
        try {
            const response = await axios.get('http://localhost:8000/api/questions');
            setQuestions(response.data);
        } catch (error) {
            console.error('Error loading questions:', error);
        }
    };

    // ค้นหาเฉลย
    const filteredSolutions = solutions.filter(solution => {
        const question = questions.find(q => q.id === solution.question_id);
        const searchLower = searchTerm.toLowerCase();
        
        return (
            solution.answer_text?.toLowerCase().includes(searchLower) ||
            question?.question_text?.toLowerCase().includes(searchLower) ||
            question?.book_id?.toLowerCase().includes(searchLower) ||
            question?.page?.toString().includes(searchLower) ||
            question?.question_no?.toString().includes(searchLower)
        );
    });

    // Pagination
    const totalPages = Math.ceil(filteredSolutions.length / itemsPerPage);
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentSolutions = filteredSolutions.slice(indexOfFirstItem, indexOfLastItem);

    // รีเซ็ตหน้าเมื่อค้นหา
    const handleSearch = (value) => {
        setSearchTerm(value);
        setCurrentPage(1);
    };

    // เปิดโมดอลแก้ไข
    const openEditModal = (solution) => {
        setEditingSolution(solution);
        setFormData({
            question_id: solution.question_id,
            answer_text: solution.answer_text || '',
            answer_img: null
        });
        setShowEditModal(true);
    };

    // ปิดโมดอลแก้ไข
    const closeEditModal = () => {
        setShowEditModal(false);
        setEditingSolution(null);
        setFormData({
            question_id: '',
            answer_text: '',
            answer_img: null
        });
    };

    // จัดการการเปลี่ยนแปลงในฟอร์ม
    const handleInputChange = (e) => {
        const { name, value, files } = e.target;
        
        if (name === 'answer_img') {
            setFormData(prev => ({
                ...prev,
                answer_img: files[0] || null
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    // อัพเดทเฉลย
    const handleUpdateSolution = async (e) => {
        e.preventDefault();
        
        if (!formData.answer_text.trim() && !formData.answer_img) {
            alert('กรุณากรอกคำตอบแบบข้อความหรืออัพโหลดรูปภาพ');
            return;
        }

        try {
            const updateData = new FormData();
            updateData.append('question_id', formData.question_id);
            updateData.append('answer_text', formData.answer_text);
            
            if (formData.answer_img) {
                updateData.append('answer_img', formData.answer_img);
            }

            await axios.put(`http://localhost:8000/api/solutions/${editingSolution.id}`, updateData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });

            alert('อัพเดทเฉลยสำเร็จ!');
            closeEditModal();
            loadSolutions();
        } catch (error) {
            console.error('Error updating solution:', error);
            alert('เกิดข้อผิดพลาดในการอัพเดทเฉลย');
        }
    };

    // ลบเฉลย
    const handleDeleteSolution = async (solutionId) => {
        if (!window.confirm('คุณแน่ใจหรือไม่ที่จะลบเฉลยนี้?')) {
            return;
        }

        try {
            await axios.delete(`http://localhost:8000/api/solutions/${solutionId}`);
            alert('ลบเฉลยสำเร็จ!');
            loadSolutions();
        } catch (error) {
            console.error('Error deleting solution:', error);
            alert('เกิดข้อผิดพลาดในการลบเฉลย');
        }
    };

    // หาข้อมูลโจทย์จาก question_id
    const getQuestionInfo = (questionId) => {
        return questions.find(q => q.id === questionId);
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-gray-100 flex items-center justify-center">
                <div className="text-xl">กำลังโหลด...</div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gray-100 p-6">
            <div className="max-w-7xl mx-auto">
                <div className="bg-white rounded-lg shadow-md p-6">
                    <h1 className="text-3xl font-bold text-gray-800 mb-6">จัดการเฉลย</h1>
                    
                    {/* ช่องค้นหา */}
                    <div className="mb-6">
                        <input
                            type="text"
                            placeholder="ค้นหาเฉลย (คำตอบ, โจทย์, รหัสหนังสือ, หน้า, ข้อ)"
                            value={searchTerm}
                            onChange={(e) => handleSearch(e.target.value)}
                            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                        />
                    </div>

                    {/* จำนวนเฉลย */}
                    {filteredSolutions.length > 0 && (
                        <div className="mb-4">
                            <p className="text-gray-600">
                                แสดง {indexOfFirstItem + 1}-{Math.min(indexOfLastItem, filteredSolutions.length)} จาก {filteredSolutions.length} รายการ (ทั้งหมด {solutions.length} รายการ)
                            </p>
                        </div>
                    )}

                    {/* ตารางเฉลย */}
                    <div className="overflow-x-auto">
                        <table className="min-w-full bg-white border border-gray-200">
                            <thead className="bg-gray-50">
                                <tr>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        ID
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        โจทย์
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        คำตอบ (ข้อความ)
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        รูปภาพคำตอบ
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        จัดการ
                                    </th>
                                </tr>
                            </thead>
                            <tbody className="bg-white divide-y divide-gray-200">
                                {currentSolutions.map((solution) => {
                                    const question = getQuestionInfo(solution.question_id);
                                    return (
                                        <tr key={solution.id} className="hover:bg-gray-50">
                                            <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                                                {solution.id}
                                            </td>
                                            <td className="px-6 py-4 text-sm text-gray-900">
                                                {question ? (
                                                    <div>
                                                        <div className="font-medium">{question.question_text}</div>
                                                        <div className="text-gray-500 text-xs">
                                                            {question.book_id} | หน้า {question.page} | ข้อ {question.question_no}
                                                        </div>
                                                    </div>
                                                ) : (
                                                    <span className="text-gray-400">ไม่พบข้อมูลโจทย์</span>
                                                )}
                                            </td>
                                            <td className="px-6 py-4 text-sm text-gray-900">
                                                {solution.answer_text ? (
                                                    <div className="max-w-xs truncate" title={solution.answer_text}>
                                                        {solution.answer_text}
                                                    </div>
                                                ) : (
                                                    <span className="text-gray-400">ไม่มีคำตอบแบบข้อความ</span>
                                                )}
                                            </td>
                                            <td className="px-6 py-4 text-sm text-gray-900">
                                                {solution.answer_img ? (
                                                    <img 
                                                        src={`http://localhost:8000/${solution.answer_img}`}
                                                        alt="Answer"
                                                        className="w-16 h-16 object-cover rounded"
                                                    />
                                                ) : (
                                                    <span className="text-gray-400">ไม่มีรูปภาพ</span>
                                                )}
                                            </td>
                                            <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                                                <div className="flex space-x-2">
                                                    <button
                                                        onClick={() => openEditModal(solution)}
                                                        className="bg-blue-500 hover:bg-blue-700 text-white px-3 py-1 rounded text-sm"
                                                    >
                                                        แก้ไข
                                                    </button>
                                                    <button
                                                        onClick={() => handleDeleteSolution(solution.id)}
                                                        className="bg-red-500 hover:bg-red-700 text-white px-3 py-1 rounded text-sm"
                                                    >
                                                        ลบ
                                                    </button>
                                                </div>
                                            </td>
                                        </tr>
                                    );
                                })}
                            </tbody>
                        </table>
                    </div>

                    {filteredSolutions.length === 0 && (
                        <div className="text-center py-8 text-gray-500">
                            ไม่พบเฉลยที่ตรงกับการค้นหา
                        </div>
                    )}

                    {/* Pagination */}
                    {totalPages > 1 && (
                        <div className="mt-6 flex justify-center items-center space-x-2">
                            <button
                                onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                                disabled={currentPage === 1}
                                className="px-3 py-2 bg-gray-300 text-gray-700 rounded-md hover:bg-gray-400 disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                                หน้าก่อน
                            </button>
                            
                            <div className="flex space-x-1">
                                {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
                                    <button
                                        key={page}
                                        onClick={() => setCurrentPage(page)}
                                        className={`px-3 py-2 rounded-md ${
                                            currentPage === page
                                                ? 'bg-blue-500 text-white'
                                                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                                        }`}
                                    >
                                        {page}
                                    </button>
                                ))}
                            </div>
                            
                            <button
                                onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                                disabled={currentPage === totalPages}
                                className="px-3 py-2 bg-gray-300 text-gray-700 rounded-md hover:bg-gray-400 disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                                หน้าถัดไป
                            </button>
                        </div>
                    )}
                </div>
            </div>

            {/* โมดอลแก้ไขเฉลย */}
            {showEditModal && (
                <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
                    <div className="relative top-20 mx-auto p-5 border w-11/12 md:w-3/4 lg:w-1/2 shadow-lg rounded-md bg-white">
                        <div className="mt-3">
                            <h3 className="text-lg font-medium text-gray-900 mb-4">
                                แก้ไขเฉลย ID: {editingSolution?.id}
                            </h3>
                            
                            <form onSubmit={handleUpdateSolution} className="space-y-4">
                                {/* แสดงข้อมูลโจทย์ */}
                                {(() => {
                                    const question = getQuestionInfo(editingSolution?.question_id);
                                    return question ? (
                                        <div className="bg-gray-50 p-4 rounded-lg">
                                            <h4 className="font-medium text-gray-800 mb-2">โจทย์:</h4>
                                            <p className="text-gray-700">{question.question_text}</p>
                                            <p className="text-gray-500 text-sm mt-1">
                                                {question.book_id} | หน้า {question.page} | ข้อ {question.question_no}
                                            </p>
                                        </div>
                                    ) : null;
                                })()}

                                {/* คำตอบแบบข้อความ */}
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        คำตอบ (ข้อความ)
                                    </label>
                                    <textarea
                                        name="answer_text"
                                        value={formData.answer_text}
                                        onChange={handleInputChange}
                                        rows={4}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                        placeholder="กรอกคำตอบแบบข้อความ"
                                    />
                                </div>

                                {/* อัพโหลดรูปภาพคำตอบ */}
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        รูปภาพคำตอบใหม่ (ถ้าต้องการเปลี่ยน)
                                    </label>
                                    <input
                                        type="file"
                                        name="answer_img"
                                        onChange={handleInputChange}
                                        accept="image/*"
                                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                    />
                                </div>

                                {/* แสดงรูปภาพปัจจุบัน */}
                                {editingSolution?.answer_img && (
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700 mb-2">
                                            รูปภาพคำตอบปัจจุบัน
                                        </label>
                                        <img 
                                            src={`http://localhost:8000/${editingSolution.answer_img}`}
                                            alt="Current Answer"
                                            className="w-32 h-32 object-cover rounded border"
                                        />
                                    </div>
                                )}

                                {/* ปุ่มจัดการ */}
                                <div className="flex justify-end space-x-3 mt-6">
                                    <button
                                        type="button"
                                        onClick={closeEditModal}
                                        className="px-4 py-2 bg-gray-300 text-gray-700 rounded-md hover:bg-gray-400"
                                    >
                                        ยกเลิก
                                    </button>
                                    <button
                                        type="submit"
                                        className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
                                    >
                                        บันทึกการแก้ไข
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdminManageSolutions;
