import React, { useState } from 'react';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000/api';

function AdminAddQuestion() {
  const [formData, setFormData] = useState({
    book_id: '',
    page: '',
    question_no: '',
    question_text: '',
    question_img: null
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [imagePreview, setImagePreview] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({
        ...prev,
        question_img: file
      }));
      
      // สร้าง preview รูปภาพ
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const removeImage = () => {
    setFormData(prev => ({
      ...prev,
      question_img: null
    }));
    setImagePreview(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      // สร้าง FormData สำหรับอัปโหลดไฟล์
      const submitData = new FormData();
      submitData.append('book_id', formData.book_id);
      submitData.append('page', parseInt(formData.page));
      submitData.append('question_no', parseInt(formData.question_no));
      submitData.append('question_text', formData.question_text || '');
      
      if (formData.question_img) {
        submitData.append('question_img', formData.question_img);
      }

      const response = await axios.post(`${API_BASE_URL}/questions`, submitData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setMessage('เพิ่มโจทย์สำเร็จแล้ว!');
      setFormData({
        book_id: '',
        page: '',
        question_no: '',
        question_text: '',
        question_img: null
      });
      setImagePreview(null);
    } catch (error) {
      setMessage('เกิดข้อผิดพลาด: ' + (error.response?.data?.detail || error.message));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto">
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold text-gray-800 mb-6">เพิ่มโจทย์ใหม่</h2>
        
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              รหัสหนังสือ (Book ID) *
            </label>
            <input
              type="text"
              name="book_id"
              value={formData.book_id}
              onChange={handleInputChange}
              required
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
              placeholder="เช่น MATH-101"
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                หน้า (Page) *
              </label>
              <input
                type="number"
                name="page"
                value={formData.page}
                onChange={handleInputChange}
                required
                min="1"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                placeholder="เช่น 25"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                ข้อที่ (Question No.) *
              </label>
              <input
                type="number"
                name="question_no"
                value={formData.question_no}
                onChange={handleInputChange}
                required
                min="1"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                placeholder="เช่น 1"
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              ข้อความโจทย์ (Question Text)
            </label>
            <textarea
              name="question_text"
              value={formData.question_text}
              onChange={handleInputChange}
              rows="4"
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
              placeholder="พิมพ์ข้อความโจทย์ที่นี่..."
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              รูปภาพโจทย์ (Question Image)
            </label>
            <div className="space-y-3">
              <input
                type="file"
                accept="image/*"
                onChange={handleImageChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 file:mr-4 file:py-2 file:px-4 file:rounded-md file:border-0 file:text-sm file:font-semibold file:bg-primary-50 file:text-primary-700 hover:file:bg-primary-100"
              />
              
              {imagePreview && (
                <div className="relative">
                  <img 
                    src={imagePreview} 
                    alt="Preview" 
                    className="max-w-full h-48 object-contain rounded-md border"
                  />
                  <button
                    type="button"
                    onClick={removeImage}
                    className="absolute top-2 right-2 bg-red-500 text-white rounded-full w-6 h-6 flex items-center justify-center text-xs hover:bg-red-600"
                  >
                    ×
                  </button>
                </div>
              )}
              
              <p className="text-sm text-gray-500">
                รองรับไฟล์ JPG, PNG, GIF ขนาดไม่เกิน 5MB
              </p>
            </div>
          </div>

          <button
            type="submit"
            disabled={loading}
            className="w-full bg-primary-600 text-white py-2 px-4 rounded-md hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-primary-500 disabled:opacity-50"
          >
            {loading ? 'กำลังบันทึก...' : 'เพิ่มโจทย์'}
          </button>
        </form>

        {message && (
          <div className={`mt-4 p-4 rounded-md ${
            message.includes('สำเร็จ') 
              ? 'bg-green-100 text-green-700 border border-green-300' 
              : 'bg-red-100 text-red-700 border border-red-300'
          }`}>
            {message}
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminAddQuestion;
