import React, { useState } from 'react';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000/api';

function AdminAddSolution() {
  const [searchData, setSearchData] = useState({
    book_id: '',
    page: '',
    question_no: ''
  });
  const [selectedQuestion, setSelectedQuestion] = useState(null);
  const [answerImg, setAnswerImg] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleSearchInputChange = (e) => {
    const { name, value } = e.target;
    setSearchData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setAnswerImg(file);
      
      // สร้าง preview รูปภาพ
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const removeImage = () => {
    setAnswerImg(null);
    setImagePreview(null);
  };

  const searchQuestion = async () => {
    setLoading(true);
    setMessage('');
    setSelectedQuestion(null);

    try {
      const response = await axios.get(
        `${API_BASE_URL}/qa/${searchData.book_id}/${searchData.page}/${searchData.question_no}`
      );
      setSelectedQuestion(response.data.question);
      if (response.data.solution) {
        setMessage('โจทย์นี้มีเฉลยแล้ว');
      }
    } catch (error) {
      setMessage('ไม่พบโจทย์ที่ระบุ');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmitSolution = async (e) => {
    e.preventDefault();
    if (!selectedQuestion) return;

    setLoading(true);
    setMessage('');

    try {
      // สร้าง FormData สำหรับอัปโหลดไฟล์
      const submitData = new FormData();
      submitData.append('question_id', selectedQuestion.id);
      
      if (answerImg) {
        submitData.append('answer_img', answerImg);
      }

      const response = await axios.post(`${API_BASE_URL}/solutions`, submitData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setMessage('เพิ่มเฉลยสำเร็จแล้ว!');
      setAnswerImg(null);
      setImagePreview(null);
      setSelectedQuestion(null);
      setSearchData({ book_id: '', page: '', question_no: '' });
    } catch (error) {
      setMessage('เกิดข้อผิดพลาด: ' + (error.response?.data?.detail || error.message));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto">
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold text-gray-800 mb-6">เพิ่มเฉลย</h2>
        
        {/* Search Question Form */}
        <div className="mb-6 p-4 bg-gray-50 rounded-lg">
          <h3 className="text-lg font-semibold text-gray-700 mb-4">ค้นหาโจทย์</h3>
          
          <div className="grid grid-cols-3 gap-4 mb-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                รหัสหนังสือ
              </label>
              <input
                type="text"
                name="book_id"
                value={searchData.book_id}
                onChange={handleSearchInputChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                placeholder="MATH-101"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                หน้า
              </label>
              <input
                type="number"
                name="page"
                value={searchData.page}
                onChange={handleSearchInputChange}
                min="1"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                placeholder="25"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                ข้อที่
              </label>
              <input
                type="number"
                name="question_no"
                value={searchData.question_no}
                onChange={handleSearchInputChange}
                min="1"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                placeholder="1"
              />
            </div>
          </div>

          <button
            onClick={searchQuestion}
            disabled={loading || !searchData.book_id || !searchData.page || !searchData.question_no}
            className="w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50"
          >
            {loading ? 'กำลังค้นหา...' : 'ค้นหาโจทย์'}
          </button>
        </div>

        {/* Selected Question Display */}
        {selectedQuestion && (
          <div className="mb-6 p-4 bg-blue-50 rounded-lg border border-blue-200">
            <h3 className="text-lg font-semibold text-blue-800 mb-2">โจทย์ที่พบ</h3>
            <p><strong>รหัสหนังสือ:</strong> {selectedQuestion.book_id}</p>
            <p><strong>หน้า:</strong> {selectedQuestion.page}</p>
            <p><strong>ข้อที่:</strong> {selectedQuestion.question_no}</p>
            {selectedQuestion.question_text && (
              <p><strong>ข้อความโจทย์:</strong> {selectedQuestion.question_text}</p>
            )}
            {selectedQuestion.question_img && (
              <div className="mt-2">
                <strong>รูปภาพโจทย์:</strong>
                <img 
                  src={selectedQuestion.question_img} 
                  alt="Question" 
                  className="mt-2 max-w-full h-auto rounded border"
                />
              </div>
            )}
          </div>
        )}

        {/* Add Solution Form */}
        {selectedQuestion && (
          <form onSubmit={handleSubmitSolution} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                รูปภาพเฉลย (Answer Image) *
              </label>
              <div className="space-y-3">
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleImageChange}
                  required
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 file:mr-4 file:py-2 file:px-4 file:rounded-md file:border-0 file:text-sm file:font-semibold file:bg-primary-50 file:text-primary-700 hover:file:bg-primary-100"
                />
                
                {imagePreview && (
                  <div className="relative">
                    <img 
                      src={imagePreview} 
                      alt="Answer Preview" 
                      className="max-w-full h-48 object-contain rounded-md border"
                    />
                    <button
                      type="button"
                      onClick={removeImage}
                      className="absolute top-2 right-2 bg-red-500 text-white rounded-full w-6 h-6 flex items-center justify-center text-xs hover:bg-red-600"
                    >
                      ×
                    </button>
                  </div>
                )}
                
                <p className="text-sm text-gray-500">
                  รองรับไฟล์ JPG, PNG, GIF ขนาดไม่เกิน 5MB
                </p>
              </div>
            </div>

            <button
              type="submit"
              disabled={loading}
              className="w-full bg-primary-600 text-white py-2 px-4 rounded-md hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-primary-500 disabled:opacity-50"
            >
              {loading ? 'กำลังบันทึก...' : 'เพิ่มเฉลย'}
            </button>
          </form>
        )}

        {message && (
          <div className={`mt-4 p-4 rounded-md ${
            message.includes('สำเร็จ') 
              ? 'bg-green-100 text-green-700 border border-green-300' 
              : message.includes('มีเฉลยแล้ว')
              ? 'bg-yellow-100 text-yellow-700 border border-yellow-300'
              : 'bg-red-100 text-red-700 border border-red-300'
          }`}>
            {message}
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminAddSolution;
